INSERT INTO datn.product (description,name,price,product_code,quantity,status,brand_id,category_id,view_count) VALUES
	 ('Ryzen 9 7950X dựa trên kiến ​​trúc mới của AMD, Zen 4. Giống như Zen 3 trước đó, AMD tiếp tục sử dụng thiết kế dựa trên chiplet trong kiến ​​trúc mới này, mang lại cho nó một giao diện cao cấp tương tự như tất cả các thiết bị trước đó Kiến trúc bộ xử lý Zen. Tuy nhiên, một số thay đổi trong kiến ​​trúc cốt lõi và việc chuyển sang quy trình sản xuất 5nm mới đã cho phép AMD tăng hiệu suất đáng kể. Nhìn chung, xu hướng của Zen 4 là giống như Zen 3, nhưng lớn hơn và mạnh mẽ hơn. Zen 4 có giao diện người dùng mạnh mẽ hơn so với kiến ​​trúc Zen 3 trước đó bao gồm phần cứng dự đoán nhánh mạnh hơn có thể đưa ra hai dự đoán nhánh cho mỗi chu kỳ.','CPU AMD Ryzen 9 7950X (4.5 GHz Upto 5.7GHz / 81MB / 16 Cores, 32 Threads / 170W / Socket AM5)',17599000,'CPUA0264',100,'1',3,10,275),
	 ('CPU AMD Ryzen 9 7900X mới ra mắt cùng với làn sóng bộ vi xử lý máy tính để bàn "Zen 4" thế hệ tiếp theo đầu tiên của AMD. Với kiến ​​trúc mới của họ, AMD không chỉ sẵn sàng trong tương lai với I / O mới mà còn cả các ứng dụng của nó, với sự hỗ trợ cho các tập lệnh mới. 7900X là bộ xử lý đa lõi thông thường 12 nhân / 24 luồng mà phần mềm không cần nhận thức đặc biệt. ','CPU AMD Ryzen 9 7900X (4.7 GHz Upto 5.6GHz / 76MB / 12 Cores, 24 Threads / 170W / Socket AM5)',11999000,'CPUA0265',100,'1',3,10,67),
	 ('CPU Intel Core i9-14900KS là con chip vi xử lý thế hệ 14, mới nhất hiện nay, là một trong những con chip mạnh nhất và hàng đầu trên thị trường hiện nay. Như chúng ta đã biết, các dòng chip CPU i9 hiện nay của Intel đang đứng đầu trên thị trường bởi hiệu năng mạnh mẽ nhất, tốc độ xử lý cao nhất và khả năng xử lý mọi tác vụ đa nhiệm, chuyên nghiệp nhanh chóng và hiệu quả nhất. Với mọi tác vụ của người dùng, các bạn sẽ không bao giờ phải lo về sự chậm trễ hay giật lag khi sử dụng CPU Intel Core i9-14900KS.','CPU Intel Core i9-14900KS (UP TO 6.2GHZ, 24 NHÂN 32 LUỒNG, 36MB CACHE, 125W) - SOCKET INTEL LGA 1700/RAPTOR LAKE',19999000,'CPUI0544',100,'1',8,10,32),
	 ('CPU Intel Core Ultra 9 285K là dòng sản phẩm cao cấp của Intel mới ra mắt với công nghệ tiên tiến nhất. Intel Core Ultra 9 285K có thông số 5.7GHz, 24 nhân 24 luồng, 36Mb Cache, 250W, tích hợp Intel Graphics. Tương thích với các dòng Mainboard Intel Z890 mới ra mắt hoặc các dòng socket LGA 1851. Là sản phẩm phù hợp với các bộ PC Gaming cao cấp hoặc các bộ PC Đồ họa chuyên nghiệp.','CPU Intel Core Ultra 9 285K TRAY NEW (Up to 5.5GHz, 24 Nhân 24 Luồng, 36MB Cache, 125W) - Socket Intel LGA 1851/Arrow Lake)',17399000,'CPUI0662',100,'1',8,10,7),
	 ('Card màn hình Asus ROG ASTRAL LC RTX 5090 32G GAMING GDDR7 OC chính là "vũ khí" các game thủ hay nhà sáng tạo cần để nâng tầm dàn PC của mình. Chiếc card đồ họa này mang đến hiệu năng vượt trội, mạnh hơn tới 30% so với dòng RTX 4090 tiền nhiệm, xử lý đồ họa và gaming đỉnh cao, mang đến trải nghiệm hình ảnh không giới hạn.','Card màn hình Asus ROG ASTRAL LC RTX 5090 32G GAMING GDDR7 OC',139999000,'VGAS0814',100,'1',5,4,17),
	 ('ASUS ROG Astral RTX 5090 32GB GDDR7 OC Edition biến huấn luyện AI thành cơn bão tốc độ với Tensor Cores thế hệ 5 và hiệu năng AI 3,593 TOPs, xử lý mô hình phức tạp nhanh như chớp! Dung lượng 32GB GDDR7 và băng thông 1,792 GB/s đảm bảo mọi thuật toán AI đều được “nuốt trôi” mượt mà, sẵn sàng đưa bạn dẫn đầu cuộc đua công nghệ!','Card màn hình Asus ROG ASTRAL RTX 5090 32G GAMING GDDR7 OC',139999000,'VGAS0812',100,'1',5,4,31),
	 ('Là một trong những chiếc card đồ họa cao cấp nhất hiện nay, Card màn hình Colorful iGame GeForce RTX 5070 Ti Ultra W OC 16GB-V mang đến hiệu năng đỉnh cao cho game thủ và nhà sáng tạo nội dung. Với kiến trúc GPU tiên tiến, bộ nhớ tốc độ cao và khả năng ép xung mạnh mẽ, sản phẩm này đáp ứng tốt các nhu cầu từ chơi game AAA đến xử lý đồ họa chuyên nghiệp. Trang bị loạt công nghệ tiên tiến như DLSS 4, Reflex và Studio, đây chính là sản phẩm lý tưởng cho những ai mong muốn chất lượng hình ảnh tốt nhất.','Card màn hình Colorful iGame GeForce RTX 5070 Ti Ultra W OC 16GB-V',33599000,'VGCL0098',100,'1',20,4,0),
	 ('SAPPHIRE PURE AMD Radeon™ RX 7000 Series được thiết kế với nguồn điện kỹ thuật số mang lại khả năng kiểm soát điện năng chính xác và hiệu suất sử dụng điện năng vượt trội.','Card màn hình SAPPHIRE PURE RX 7800 XT GAMING OC 16GB',17399000,'VGSP0059',100,'1',22,4,14),
	 ('','Mainboard ASUS TUF GAMING Z890-PLUS WIFI DDR5',9999000.0,'MBAS0829',100,'1',5,2,3),
	 ('','Mainboard ASUS ROG MAXIMUS Z890 HERO DDR5',23199000,'MBAS0824',100,'1',5,2,3);
INSERT INTO datn.product (description,name,price,product_code,quantity,status,brand_id,category_id,view_count) VALUES
	 ('Main Asus B760M-AYW WIFI D5 là dòng bo mạch chủ mới của Asus với tính năng hiện đại, tiên tiến cùng công nghệ vượt trội, giúp cho máy tính của bạn hoạt động trơn tru, mượt mà hơn. Có thể nói đây là phiên bản hoàn hảo từ vẻ đẹp bên ngoài đến cấu hình bên trong, hứa hẹn sẽ là sản phẩm đáng mong đợi cho những tín đồ công nghệ.','Mainboard Asus B760M-AYW WIFI DDR5',4199000.0,'MBAS0795',100,'1',5,2,6),
	 ('','RAM Desktop KINGSTON Fury Beast RGB (KF556C40BBAK2-64) 64GB (2x32GB) DDR5 5600MHz',11999000,'RAKT0324',100,'1',12,5,0),
	 ('Ram Desktop Corsair DOMINATOR PLATINUM RGB Black là thế hệ DDR5 của dòng Dominator lừng lẫy với cải tiến lớn về hiệu năng','RAM Desktop Corsair Vengeance RGB (CMT32GX5M2X6000C36) 32GB (2x16GB) DDR5 6000MHz',5899000.0,'RACO0387',10,'1',18,5,2),
	 ('Được xây dựng cho các hệ thống bảo mật hoạt động 24/7 với độ phân giải cao. Tốc độ tải công việc được hỗ trợ lên tới 180 TB / năm 1 và hỗ trợ tới 64 camera, các ổ WD Purple được tối ưu hóa cho các hệ thống giám sát. Các ổ WD Purple 8TB, 10TB & 12TB được thiết kế để hỗ trợ phân tích Deep Learning trong các NVR có khả năng AI và có xếp hạng khối lượng công việc nâng cao lên tới 360TB / năm.','Ổ Cứng HDD WD Purple 6TB 3.5 inch, 5640RPM, SATA III, 256MB Cache (WD64PURZ)',5999000.0,'HDWD0485',100,'1',16,6,0),
	 ('Dòng sản phẩm HDD Seagate IronWolf Pro là một lựa chọn tuyệt vời cho việc nâng cấp các ổ cứng lưu trữ mạng(NAS). Seagate IronWolf Pro kết hợp công nghệ flash NAND mới nhất với ổ cứng truyền thống cho hiệu năng và độ bền bỉ khi thực hiện tải công việc nặng nề.','Ổ Cứng HDD SEAGATE Ổ cứng HDD Seagate Ironwolf Pro 14TB 3.5 inch, 7200RPM, SATA, 256MB Cache (ST14000NE0008)10TB 3.5 inch, 7200RPM ,SATA III3, 256MB Cache (ST10000VN000)',16899000,'HDSE0206',100,'1',15,6,3),
	 ('SSD WD_BLACK SN850X – dòng ổ cứng thể rắn cao cấp của Western Digital, được thiết kế tối ưu cho game thủ, nhà sáng tạo nội dung và người dùng chuyên nghiệp cần hiệu suất vượt trội.','Ổ Cứng SSD WD SN850X Black 8TB – M.2 2280 PCIe Gen4 x4 (Đọc 7300MB/s - Ghi 6300MB/s) - (WDS800T2X0E)',21999000,'HDWD0543',10,'1',16,7,0),
	 ('Ổ cứng SSD Samsung 870 QVO là dòng ổ cứng tuyệt vời dành cho mọi đối tượng khách hàng với rất nhiều ưu điểm: Dễ tương thích (kết nối SATA III), Bền bỉ (V-Nand MLC), thương hiệu danh tiếng & hơn hết là giá bán dễ tiếp cận. ','Ổ Cứng SSD Samsung 870 QVO 4TB 2.5 inch SATA III (Đọc 560MB/s - Ghi 530MB/s) - (MZ-77Q4T0BW)',12999000,'HDSA0216',100,'1',21,7,0),
	 ('ROG Hyperion GR701 mang đến nhân tố X cả về hình thức và chức năng. Với sự hỗ trợ cho bộ tản nhiệt kép 420 mm, card màn hình cao chót vót, ngăn chứa card đồ họa bằng nhôm 2 chiều được tích hợp đầy đủ, bộ sạc thiết bị 60 watt và hai đầu nối USB-C phía trước cho các bo mạch chủ ROG tương thích.','Vỏ Case Asus ROG Hyperion GR701  (Full Tower / Màu Đen )',11489000,'CSAS0017',100,'1',5,8,2),
	 ('','Vỏ Case ASUS A21 BLK (Mid Tower / Màu Đen )',1999000.0,'CSAS0018',100,'1',5,8,3),
	 ('Nguồn ASUS ROG THOR 1600W Titanium III - 1600W hiệu suất cao, độ ổn định tuyệt đối, và vẻ ngoài đậm chất gaming, thì ASUS ROG THOR 1600W Titanium III - 1600W chắc chắn là lựa chọn không thể bỏ qua. Đây là sự đầu tư xứng đáng cho những hệ thống cần sức mạnh tối đa và độ tin cậy tuyệt đối.','Nguồn ASUS ROG THOR 1600W Titanium III - 1600W (80 Plus Titanium/ATX3.1/PCIE5.1)',27999000,'PWAS0038',100,'1',5,9,0);
INSERT INTO datn.product (description,name,price,product_code,quantity,status,brand_id,category_id,view_count) VALUES
	 ('','Nguồn máy tính MSI MAG A650BN 650W ( 80 Plus Bronze/Màu Đen)',1699000.0,'PWMI0005',100,'1',10,9,0),
	 ('Intel Core Ultra 7 265K sở hữu 20 nhân và 20 luồng, mang đến khả năng xử lý vượt trội trong các tác vụ đa nhiệm. CPU này đặc biệt hữu ích cho những người thường xuyên làm việc với các ứng dụng nặng như phần mềm đồ họa, chỉnh sửa video hay streaming. So với các CPU cùng phân khúc, số lượng nhân và luồng lớn giúp Core Ultra 7 265K dễ dàng đáp ứng các tác vụ đa nhiệm mà không gặp hiện tượng "thắt cổ chai".','CPU Intel Core Ultra 7 265K (UP TO 5.5Ghz, 20 NHÂN 20 LUỒNG, 30MB CACHE, 125W) - Socket Intel LGA 1851/ARROW LAKE',12999000,'CPUI0552',10,'1',8,10,6),
	 ('AMD Ryzen 5 8500G với cấu trúc lai và APU được trang bị 6 nhân/12 luồng cho ra xung nhịp cơ bản 3.5GHz và đạt 5.0GHz ở giai đoạn Boost; Bộ đệm L3 là 16 MB trong khi Bộ đệm L2 là 6 MB. Thông số cơ bản mà chiếc CPU này đem lại sẽ thật sự làm bạn ấn tượng khi trải nghiệm thực tế. ','CPU AMD Ryzen 5 8500G (3.5GHz Upto 5.0GHz / 22MB / 6 Cores, 12 Threads / 65W / Socket AM5)',5999000.0,'CPUA0284',10,'1',3,10,3),
	 ('CPU AMD Ryzen 9 9900X được xây dựng trên kiến trúc Zen 5 mới nhất của AMD, mang đến hiệu năng vượt trội so với thế hệ trước. Kiến trúc này tối ưu hóa khả năng xử lý đa luồng, giúp CPU AMD Ryzen 9 9900X xử lý nhiều tác vụ cùng lúc một cách mượt mà.','CPU AMD Ryzen 9 9900X (4.4 GHz Upto 5.6GHz / 78MB / 12 Cores, 24 Threads / 120W / Socket AM5)',13999000,'CPUA0294',10,'1',3,10,6),
	 ('Phân khúc CPU tầm trung sẽ chẳng còn yên ả với sự xuất hiện của Intel Core i5-10400, nhờ khắc phục toàn bộ những yếu điểm mà i5-9400 bị người dùng "chê" từ hồi năm ngoái','CPU Intel Core i5-10400 (Tray, Cũ đẹp)',3099000.0,'CPUI00006',10,'1',8,10,6),
	 ('CPU Intel Core i5-12400 là CPU thế hệ thứ 12 của Intel (Alder Lake) trên nền Socket LGA 1700 với kiến trúc hoàn toàn mới cho hiệu năng vượt trội so với người tiền nhiệm.','CPU Intel Core i5-12400 TRAY NEW (Upto 4.4GHz, 6 Nhân 12 Luồng, 18MB Cache, 65W) - Socket Intel LGA 1700)',3999000.0,'CPUI0524',10,'1',8,10,6),
	 ('CPU Intel Core i3-10105 là phiên bản nâng cấp của I3-10100 với xung nhịp tăng nhẹ và hiệu suất trên mỗi nhân được cải thiện. Với 4 nhân 8 luồng, đây là CPU có hiệu năng trên giá thành tốt nhất của Intel ở phân khúc phổ thông. 
